mapper.update:
UPDATE sys_user SET
	STATUS = #{status},
	UPDATE_DATE = #{updateDate}
WHERE 
	ID = #{id}

mapper.select:
SELECT
from user
username != 'admin'
AND STATUS=#{status}
AND STATUS!='DELETED'
AND INSERT_DATE >= #{startDate}
AND INSERT_DATE <= #{endDate}
AND ( USERNAME LIKE "%"#{keywords}"%" or NICKNAME LIKE "%"#{keywords}"%" or FIRST_NAME LIKE "%"#{keywords}"%" or LAST_NAME LIKE "%"#{keywords}"%" or EMAIL LIKE "%"#{keywords}"%" or PHONE LIKE "%"#{keywords}"%" )
ORDER BY ${orderField} ${orderDirection}
order by INSERT_DATE DESC

select count(0) as c from user




		 AND STATUS=#{status}  


		 AND STATUS!='DELETED'




		 AND INSERT_DATE >= #{startDate} 


		 AND INSERT_DATE <= #{endDate} 


		AND (
			USERNAME LIKE "%"#{keywords}"%"
			or NICKNAME LIKE "%"#{keywords}"%"
			or FIRST_NAME LIKE "%"#{keywords}"%"
			or LAST_NAME LIKE "%"#{keywords}"%"
			or EMAIL LIKE "%"#{keywords}"%"
			or PHONE LIKE "%"#{keywords}"%"
		)
